import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    // Add your login logic here (e.g., send login credentials to the server for validation)
    // For this example, let's assume successful login redirects to a dashboard page
    // Replace '/dashboard' with the actual path to your dashboard page
    if (username !== '' && password !== '') {
      // Redirect to the dashboard page after successful login
      window.location.href = '/dashboard';
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <div className="input-group">
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div className="input-group">
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      <div className="button-group">
        <button onClick={handleLogin}>Login</button>
        <Link to="/signup">Sign Up</Link>
        <Link to="/">Cancel</Link>
      </div>
    </div>
  );
};

export default Login;

