import React, { useState, useEffect } from 'react';
import '../styles/Testimonials.css'; // Import custom CSS file for Testimonials styles

const testimonialsData = [
  {
    id: 1,
    name: 'John Doe',
    review: 'This app is amazing! It has helped me find the perfect orphanage to support. Highly recommended!',
  },
  {
    id: 2,
    name: 'Jane Smith',
    review: 'I love the user-friendly interface of this app. It makes it easy to connect with orphanages and make a difference.',
  },
  // Add more testimonial objects as needed
];

const Testimonials = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // Automatically rotate testimonials every 3 seconds
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % testimonialsData.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const testimonialCards = testimonialsData.map((testimonial, index) => (
    <div
      key={testimonial.id}
      className={`testimonial__card ${index === currentIndex ? 'active' : ''}`}
    >
      <h3 className="testimonial__name">{testimonial.name}</h3>
      <p className="testimonial__review">{testimonial.review}</p>
    </div>
  ));

  return (
    <div className="testimonials__carousel">
      {testimonialCards}
    </div>
  );
};

export default Testimonials;
